const express = require('express');
const bodyParser = require('body-parser');
const mongoose = require('mongoose');
const passport = require('passport');

//Routing


const register = require('./src/routes/user.route');
const login = require('./src/routes/user.route');
const addcourses = require('./src/routes/course.route');
const bookscat = require('./src/routes/bookcat.route');
const getbookcat = require('./src/routes/bookcat.route');
const enrollStudent = require('./src/routes/enrollCourse.route');


//import register from './src/routes/user.route.js';
//import login from'./src/routes/user.route.js';
//import addcourses from'./src/routes/course.route.js';
//import bookscat from'./src/routes/bookcat.route';
//import getbookcat from'./src/routes/bookcat.route';
//import enrollCourse  from'./src/routes/enrollCourse.route';
//import Register from './src/components/Register';
// import { default: Register }  from './src/components/Register';

//Express
const app = express();

// Database connection
const config = require("./db").mongoURI;

mongoose
    .connect(config,
            { useNewUrlParser: true })
    .then(() => console.log("MongoDB successfully connected"))
    .catch(err => console.log(err));
    
mongoose.Promise = global.Promise;

const db = mongoose.connection;
db.on('error', console.error.bind(console, 'MongoDB connection error:'));

//Body Parser-Middleware
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({extended: false}));

//Passport Initialization
app.use(passport.initialize());
require('./passport')(passport);


app.use('/login',login);
app.use('/register',register);
app.use('/addCourses',addcourses);
app.use('/api/library', bookscat);
app.use('/api/library', getbookcat);
app.use('/enrollCourse', enrollStudent);

//PORT
let port = 5000;

app.listen(port, () => {
    console.log('Server is up and running on port number ' + port);
});

