import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { newcourses,logoutUser, getBookCat } from '../../actions/authentication';

class TeacherAddCourse extends Component{    
    
    constructor(props){
        super(props);

        this.state={
            course_id: "",
            course_name: "",
            teacher_name: "",
            book_cat: "",
            course_size: "1",
            status: false
        }
    }

    onChange = e => {
        this.setState({ 
            [e.target.id]: e.target.value 
        });
    };
    
    onLogoutClick = e => {
        e.preventDefault();
        this.props.logoutUser();
    };
    
    onSubmit = e => {
        e.preventDefault();
        const add_course = {
            course_id: this.state.course_id,
            course_name: this.state.course_name,
            teacher_name: this.state.teacher_name,
            book_cat: this.state.book_cat,
            course_size: this.state.course_size,
            status: this.state.status
        };
        this.props.newcourses(add_course, this.props.history);
        
    };

    componentDidMount() {
        
        const {getBookCat} = this.props;
        getBookCat();

      }

    render(){
        const {schema} = this.props.courses;

        return(
            <div className="addcourses" >
                <div className="row">
                    <div className="col-md-12">
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <input type="number"
                                    min="1" 
                                    onChange={this.onChange}
                                    className="form-control" 
                                    id="book_id" 
                                    placeholder="Book Id" 
                                    name="book_id"
                                    value={this.state.course_id}
                                    />
                            </div>
                            <div className="form-group">
                                <input type="text" 
                                    onChange={this.onChange}
                                    className="form-control" 
                                    id="book_name" 
                                    placeholder="Book Name" 
                                    name="book_name"
                                    value={this.state.course_name}
                                    />
                            </div>
                            <div className="form-group">
                                <input type="text"
                                    onChange={this.onChange} 
                                    className="form-control" 
                                    id="author_name" 
                                    placeholder="Author Name" 
                                    name="author_name"
                                    value={this.state.teacher_name}
                                    />
                            </div>
                            <div className="form-group">
                                <select type="text" 
                                    onChange={this.onChange}
                                    className="form-control" 
                                    id="book_cat" 
                                    placeholder="Book Category" 
                                    name="book_cat"
                                    value={this.state.book_cat}
                                >   
                                  
                                  {
                                      schema.length > 0 && schema.map((el) =>{
                                        return <option key={el}>{el}</option>
                                      })
                                  }
                                </select>
                            </div>
                            <div className="form-group">
                                <input type="number" 
                                    onChange={this.onChange} 
                                    min="1" 
                                    className="form-control" 
                                    id="quantity" 
                                    placeholder="Quantity" 
                                    name="quantity"
                                    value={this.state.course_size}
                                    />
                            </div>
                            <div className="form-group">
                                <input type="text" 
                                    onChange={this.onChange} 
                                    min="1" 
                                    className="form-control" 
                                    id="status" 
                                    name="status"
                                    readOnly
                                    value={this.state.status === true ? "Active" : "Inactive"}
                                    />
                            </div>
                            <button type="submit" className="btn btn-success">Add New Course</button>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}

TeacherAddCourse.propTypes = {
    newCourses: PropTypes.func.isRequired,
    logoutUser: PropTypes.func.isRequired,
    getBookCat: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors,
    courses: state.courses
});


export default connect(
    mapStateToProps,
    {newcourses, logoutUser, getBookCat}
  )(withRouter(TeacherAddCourse));
