// //models >> books.js

const mongoose = require('mongoose');
const Schema = mongoose.Schema;

//Books Schema
let CoursesSchema = new Schema({
    course_id:{
        type: String,
        required: true
    },
    course_name: {
        type: String,
        required: true, 
        max: 20
    },
    course_teacher:{
        type: String, 
        required: true
    },
    course_size:{
        type: String, 
        required: true
    },
    editedBy:{
        type:Object,
        default: {
            "name":"Admin"
        }
    }
});


// // Export the model
exports.require = mongoose.model('Courses', CoursesSchema);

