//models >> issueBook.js

const mongoose = require('mongoose');
const Schema = mongoose.Schema;

//Issued Book Schema
let EnrollStudentSchema = new Schema({
    Students:{
        type: Schema.Types.ObjectId,
        ref: 'Students'
    },
    Courses: {
        type: Schema.Types.ObjectId,
        ref: 'Courses'
    },
    EnrollDate: {
        type: Date,
        default: Date.now
    },
    graduationDate: {
        type: Date
    }
});


// Export the model
module.exports = mongoose.model('enrollStudent', EnrollStudentSchema);