import { GET_BOOKCAT, GET_COURSES, DELETE_COURSE, 
    DELETE_BOOKCAT, FETCH_BOOKCAT, GET_ENROLLED_COURSES } from '../actions/types';


const initialState = {
    schema: [],
    viewCourse: [],
    bookCat: [],
    enrolledCourses: []
}

export default function(state = (initialState), action ) {
    switch(action.type) {  
        case GET_BOOKCAT:
            return {
                ...state, 
                schema: action.payload
            };
        case FETCH_BOOKCAT:
                return {
                    ...state, 
                    bookCat: action.payload
                };
        case GET_COURSES:
            return{
                ...state,
                viewCourse: action.payload
            };
        case DELETE_COURSE:
            return { 
                viewCourse: state.viewCourse.filter(viewCourse =>
                    viewCourse._id !== action.payload._id )
            };
        case DELETE_BOOKCAT:
            return {
                ...state,
                schema: state.schema.filter(schema =>
                    schema._id !== action.payload._id )
            };
        case GET_ENROLLED_COURSES:
            return{
                ...state,
                enrolledCourses: action.payload
            };
        default: 
            return state;
    }
}